allow:
Bash(powershell.exe "
Write-Host ''''
Write-Host ''=== D: DRIVE STATUS ==='' -ForegroundColor Cyan
$disk = Get-WmiObject Win32_LogicalDisk -Filter ""DeviceID=''D:''""
if ($disk) {
    $total = [math]::Round($disk.Size / 1GB, 2)
    $free = [math]::Round($disk.FreeSpace / 1GB, 2)
    $used = [math]::Round(($disk.Size - $disk.FreeSpace) / 1GB, 2)
    Write-Host ""Total: $total GB | Used: $used GB | Free: $free GB""
} else {
    Write-Host ''D: niet gevonden''
}
")
